import React from "react";
import { useState } from "react";
import { useContext } from "react";
import toast from "react-hot-toast";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { PropagateLoader } from "react-spinners";
import { AuthContext } from "../../Context/AuthContext/UserContext";

const RegisterPage = () => {
  const [animation, setAnimation] = useState(false);
  const { signUp, updateUser } = useContext(AuthContext);
  const [error, setError] = useState("");

  const location = useLocation();
  const from = location.state?.from?.pathname || "/";
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    const form = e.target;
    const name = form.name.value;
    const email = form.email.value;
    const password = form.password.value;

    signUp(email, password)
      .then((result) => {
        const user = result.user;
        form.reset();
        toast.success("Register SuccessFully");
        console.log(
          "ðŸš€ ~ file: RegisterPage.js ~ line 21 ~ handleSubmit ~ user",
          user
        );
        setAnimation(true);
        const userInfo = {
          displayName: name,
        };
        updateUser(userInfo)
          .then(() => {
            saveUser(name, email);
          })
          .catch((err) => console.log(err));
      })
      .catch((error) => {
        console.log("error", error);
        setError(error.message);
      });

    console.log(email, password);
  };

  const saveUser = (name, email) => {
    console.log(name, email);
    const user = {
      name,
      email,
    };
    fetch(`https://hotel-web-server.vercel.app/user/${email}`, {
      method: "PUT",
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify(user),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.status === "success") {
          console.log(data);
          localStorage.setItem("Radission BluToken", data.data);
          setTimeout(() => {
            navigate(from, { replace: true });
          }, 300);
        }
      });
  };

  return (
    <div className="flex justify-center bg-slate-900">
      {animation ? (
        <div className="h-[600px] flex justify-center items-center">
          <PropagateLoader color="#ffffff" />
        </div>
      ) : (
        <>
          <div className="w-full max-w-md p-8 space-y-3 shadow-lg my-10 rounded-xl bg-gray-800 text-gray-100">
            <h1 className="text-2xl font-bold text-center">Register</h1>
            <form
              onSubmit={handleSubmit}
              noValidate=""
              action=""
              className="space-y-6 ng-untouched ng-pristine ng-valid"
            >
              <div className="space-y-1 text-sm">
                <label htmlFor="email" className="block text-gray-400">
                  Name
                </label>
                <input
                  type="text"
                  name="name"
                  id="username"
                  placeholder="Name"
                  className="w-full px-4 py-3 rounded-md border-gray-700 bg-gray-900 text-gray-100 focus:border-violet-400"
                />
              </div>

              <div className="space-y-1 text-sm">
                <label htmlFor="email" className="block text-gray-400">
                  Email
                </label>
                <input
                  type="text"
                  name="email"
                  id="username"
                  placeholder="Email"
                  className="w-full px-4 py-3 rounded-md border-gray-700 bg-gray-900 text-gray-100 focus:border-violet-400"
                />
              </div>
              <div className="space-y-1 text-sm">
                <label htmlFor="password" className="block text-gray-400">
                  Password
                </label>
                <input
                  type="password"
                  name="password"
                  id="password"
                  placeholder="Password"
                  className="w-full px-4 py-3 rounded-md border-gray-700 bg-gray-900 text-gray-100 focus:border-violet-400"
                />
              </div>
              <p className="text-red-500">{error}</p>
              <button
                type="submit"
                className="block w-full p-3 text-center rounded-sm text-white font-semibold bg-violet-600"
              >
                Register
              </button>
            </form>

            <p className="text-xs text-center sm:px-6 text-gray-400">
              Already Have an Account?
              <Link
                rel="noopener noreferrer"
                to="/login"
                className="underline text-gray-100"
              >
                Sign In
              </Link>
            </p>
          </div>
        </>
      )}
    </div>
  );
};

export default RegisterPage;
